import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import List from './List';

function App() {
  const [error, setError] = useState();
  const [isLoaded, setIsLoaded] = useState(false);
  const [people, setPeople] = useState([]);
  const [birthdayPeople, setBirthdayPeople] = useState([
    people.filter((person) => person.dob.date.slice(5, 10) === today),
  ]);

  useEffect(() => {
    fetch('https://randomuser.me/api/?results=1000&inc=name,dob,picture,login')
      .then((res) => res.json())
      .then(
        (data) => {
          setIsLoaded(true);
          setPeople(data.results);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  let today = new Date().toJSON().slice(5, 10);

  const handleClick = (value) => {
    today = value.toJSON().slice(5, 10);
    setBirthdayPeople(
      people.filter((person) => person.dob.date.slice(5, 10) === today)
    );
  };

  if (error || !Array.isArray(people)) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <main>
        <section className="container">
          <h3>birthdays today</h3>
          <Calendar onChange={handleClick} />
          <List people={birthdayPeople} />
        </section>
      </main>
    );
  }
}

export default App;
