import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import List from './List';

function App() {
  const [error, setError] = useState();
  const [isLoaded, setIsLoaded] = useState(false);
  const [people, setPeople] = useState([]);
  const [birthdayPeople, setBirthdayPeople] = useState([]);
  const [today, setToday] = useState();
  const [clickedDate, setClickedDate] = useState(today);

  useEffect(() => {
    fetch('https://randomuser.me/api/?results=1000&inc=name,dob,picture,login')
      .then((res) => res.json())
      .then(
        (data) => {
          setIsLoaded(true);
          setPeople(data.results);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  // let today = new Date().toString().slice(4, 10);
  // let clickedDate;
  useEffect(() => {
    setToday(new Date().toString().slice(4, 10));
    setBirthdayPeople(
      people.filter(
        (person) =>
          new Date(
            new Date(person.dob.date).getTime() +
              new Date(person.dob.date).getTimezoneOffset() * 60000
          )
            .toString()
            .slice(4, 10) === today
      )
    );
  }, [people, today]);

  const handleClick = (value) => {
    setClickedDate(value.toString().slice(4, 10));
    console.log(value, today, clickedDate);
    let filteredBirthday = people.filter(
      (person) =>
        new Date(
          new Date(person.dob.date).getTime() +
            new Date(person.dob.date).getTimezoneOffset() * 60000
        )
          .toString()
          .slice(4, 10) === clickedDate
    );
    setBirthdayPeople(filteredBirthday);
    console.log(birthdayPeople);
  };

  if (error || !Array.isArray(people)) {
    return <div className="errorScreen">Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div className="loadingScreen">Loading...</div>;
  } else {
    // clickedDa/te = today;
    return (
      <>
        <main>
          <div>
            <section className="container today">
              <h1>Birthday Reminder!</h1>
              {clickedDate === today ? (
                <p>It's {today} today</p>
              ) : (
                <p>Birthday on {clickedDate}</p>
              )}

              <List people={birthdayPeople} />
            </section>
          </div>
          <div>
            <section className="container calendar">
              <Calendar className="c1" onChange={handleClick} />
            </section>
            <section className="container upcoming">
              {/* <List people={upcomingBirthday} /> */}
            </section>
          </div>
        </main>
      </>
    );
  }
}

export default App;
